import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import ReactPageScroller from "react-page-scroller";
import { Nav, 
        NavItem, 
        NavLink } from 'reactstrap';
import { Pager } from "react-bootstrap";

import 'bootstrap/dist/css/bootstrap.css';
import './Portfolio.css';
import Intro from './Components/Intro/Intro';
import AboutMe from './Components/AboutMe/AboutMe';
import JobHistory from './Components/JobHistory/JobHistory';
import References from './Components/References/References';
import PastProjects from './Components/PastProjects/PastProjects';
import CurrProjects from './Components/CurProjects/CurProjects';

class Portfolio extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentPage: null,
      pageCont: []
    };

  }

  buildPage(){
    // var navigation = [];
    const match = window.matchMedia(`(max-width: 767px)`);

    if(match){
      alert("Mobile BF: " + match.matches);
      const navigation = this.MobileNavigation();
      return [...navigation];
    }else{
      alert("Mobile BF: " + match.matches);
      const navigation = this.DesktopNavigation();
      return [...navigation];
    }
  }

  //If the user is on a mobile device:
  checkIfMobile(mq){
    const pageNumbers = [];

    if (window.matchMedia('(max-width: 767px)')){
      pageNumbers.push(
        <div className="navContainer">
          <NavItem className="navItemDesign">
            <strong><NavLink className="navLinkDesign" href="#" onClick={() => this.changePage("Prev")}><p>Previous</p></NavLink></strong>
          </NavItem>
          <NavItem className="navItemDesign">
            <strong><NavLink className="navLinkDesign" href="#" onClick={() => this.changePage("Next")}><p>Previous</p></NavLink></strong>
          </NavItem>
        </div>
      );
    }else{
      var pageTitles = ["Intro", "About Me", "Job History", "References", "Past Projects", "Current Projects", "Further Details"];
      for (let i = 0; i <= 5; i++) {
        pageNumbers.push(
          <NavItem className="navItemDesign">
            <strong><NavLink className="navLinkDesign" href="#" onClick={() => this.handlePageChange(i)}><p id={i}>{pageTitles[i]}</p></NavLink></strong>
          </NavItem>
        );
      }
    }
    return [...pageNumbers];
  }

  DesktopNavigation(){
    alert("DESKTOP FUNCTION");
    const navSection = [];
    const pageNumbers = this.getPagesNumbers();

    navSection.push(
      <React.Fragment>
        <ReactPageScroller
        pageOnChange={this.handlePageChange}
        customPageNumber={this.state.currentPage}>
          <Intro />
          <AboutMe />
          <JobHistory />
          <References />
          <PastProjects />
          <CurrProjects />
        </ReactPageScroller>
        <Nav pills className="nav-section" bsSize="large">
          {pageNumbers}
        </Nav>
      </React.Fragment>
    );

    return [...navSection];
  }

  MobileNavigation(){
    alert("MOBILE FUNCTION");
    const navSection = [];
    const pageNumbers = this.getPageButtons();

    navSection.push(
      <React.Fragment>
        <ReactPageScroller
          blockScrollUp={true}
          blockScrollDown={true}
          customPageNumber={this.state.currentPage}>
          <Intro />
          <AboutMe />
          <JobHistory />
          <References />
          <PastProjects />
          <CurrProjects />
        </ReactPageScroller>
        <Nav pills className="nav-section" bsSize="large">
          {pageNumbers}
        </Nav>
      </React.Fragment>
    );

    return [...navSection];
  }

  handlePageChange = number => {
    if(number === 0){
      document.getElementById("0").style.borderBottom = '2px solid white';
      document.getElementById("1").style.borderBottom = '0px';
      document.getElementById("2").style.borderBottom = '0px';
      document.getElementById("3").style.borderBottom = '0px';
      document.getElementById("4").style.borderBottom = '0px';
      document.getElementById("5").style.borderBottom = '0px';
    }else if(number === 1){
      document.getElementById("0").style.borderBottom = '0px';
      document.getElementById("1").style.borderBottom = '2px solid white';
      document.getElementById("2").style.borderBottom = '0px';
      document.getElementById("3").style.borderBottom = '0px';
      document.getElementById("4").style.borderBottom = '0px';
      document.getElementById("5").style.borderBottom = '0px';
    }else if(number === 2){
      document.getElementById("0").style.borderBottom = '0px';
      document.getElementById("1").style.borderBottom = '0px';
      document.getElementById("2").style.borderBottom = '2px solid white';
      document.getElementById("3").style.borderBottom = '0px';
      document.getElementById("4").style.borderBottom = '0px';
      document.getElementById("5").style.borderBottom = '0px';
    }else if(number === 3){
      document.getElementById("0").style.borderBottom = '0px';
      document.getElementById("1").style.borderBottom = '0px';
      document.getElementById("2").style.borderBottom = '0px';
      document.getElementById("3").style.borderBottom = '2px solid white';
      document.getElementById("4").style.borderBottom = '0px';
      document.getElementById("5").style.borderBottom = '0px';
    }else if(number === 4){
      document.getElementById("0").style.borderBottom = '0px';
      document.getElementById("1").style.borderBottom = '0px';
      document.getElementById("2").style.borderBottom = '0px';
      document.getElementById("3").style.borderBottom = '0px';
      document.getElementById("4").style.borderBottom = '2px solid white';
      document.getElementById("5").style.borderBottom = '0px';
    }else if(number === 5){
      document.getElementById("0").style.borderBottom = '0px';
      document.getElementById("1").style.borderBottom = '0px';
      document.getElementById("2").style.borderBottom = '0px';
      document.getElementById("3").style.borderBottom = '0px';
      document.getElementById("4").style.borderBottom = '0px';
      document.getElementById("5").style.borderBottom = '2px solid white';
    }

    this.setState({ currentPage: number }); // set currentPage number, to reset it from the previous selected.
  };

  changePage = direction => {
    if(direction === "Prev"){
      alert("Previous");
    }else if(direction === "Next"){
      alert("Next");
    }
  }

  getPagesNumbers = () => {
    const pageNumbers = [];
    var pageTitles = ["Intro", "About Me", "Job History", "References", "Past Projects", "Current Projects", "Further Details"];
    // var pageNames = ["Intro", "AboutMe", "JobHistory", "References", "PastProjects", "CurProjects", "FurtherDetails"];

    for (let i = 0; i <= 5; i++) {
      pageNumbers.push(
        <NavItem className="navItemDesign">
          <strong><NavLink className="navLinkDesign" href="#" onClick={() => this.handlePageChange(i)}><p id={i}>{pageTitles[i]}</p></NavLink></strong>
        </NavItem>
      );
    }

    return [...pageNumbers];
  };

  getPageButtons = () => {
    const pageNumbers = [];

    pageNumbers.push(
      <div>
        <NavItem className="navItemDesign">
          <strong><NavLink className="navLinkDesign" href="#" onClick={() => this.changePage("Prev")}><p>Previous</p></NavLink></strong>
        </NavItem>
        <NavItem className="navItemDesign">
          <strong><NavLink className="navLinkDesign" href="#" onClick={() => this.changePage("Next")}><p>Previous</p></NavLink></strong>
        </NavItem>
      </div>
    );

    return [...pageNumbers];
  };

  componentDidMount(){
    document.title = "Christopher Littlewood - Portfolio";

    //Below this is where I'd like to use the media query to set the sidenavOPen to false. Just not sure how to achieve that
    this.checkWidth = () => {
      var pageCont = [];

      const match = window.matchMedia(`(max-width: 767px)`);
      if( match === 'true'){
        // alert("Mobile CDM: " + match.matches);
        pageCont = this.MobileNavigation();
      }else{
        // alert("Mobile CDM: " + match.matches);
        pageCont = this.DesktopNavigation();
      }

      this.setState({
        pageCont: pageCont
      });
    };

    this.checkWidth();
    window.addEventListener("resize", this.checkWidth);
  }

  goToPage = (pageNumber) => {
    this.reactPageScroller.goToPage(pageNumber);
  }
  
  render() {
    // const navigationSection = this.buildPage();

    return (
      <div>
        {this.state.pageCont}
      </div>
        
    );
  }
}

export default Portfolio;
